void solve(ListNode* root) {
   /*
   The LinkedList is defined as:
   root-> val = value of the node
   root-> next = address of next element from the node
   The List is 1 -> 2 -> 3 -> 4 -> 5
   */
   int sum = 0;
   while(root -> next != NULL) {
       sum += root -> val;
       root = root -> next;
   }
   cout << sum << endl;
}

